macro.anyXML =
   element * - (w50067aa:* | d50091e7a1480:egXML)
   {
      attribute * { text }*,
      ( text | macro.anyXML )*
   }

element TEI
{
   att.global.attributes,
   attribute version { token { pattern = "[\d]+(\.[\d]+){0,2}" } }?,
   ( teiHeader, ( ( model.resourceLike+, text? ) | text ) )
}

