element binaryObject
{
   att.global.attributes,
   att.internetMedia.attributes,
   attribute width
   {
      token
      {
         pattern = "[\-+]?\d+(\.\d+)?(%|cm|mm|in|pt|pc|px|em|ex|gd|rem|vw|vh|vm)"
      }
   }?,
   attribute height
   {
      token
      {
         pattern = "[\-+]?\d+(\.\d+)?(%|cm|mm|in|pt|pc|px|em|ex|gd|rem|vw|vh|vm)"
      }
   }?,
   attribute scale
   {
      xsd:double | token { pattern = "(\-?[\d]+/\-?[\d]+)" } | xsd:decimal
   }?,
   attribute encoding
   {
      list
      {
         token { pattern = "(\p{L}|\p{N}|\p{P}|\p{S})+" },
         token { pattern = "(\p{L}|\p{N}|\p{P}|\p{S})+" }*
      }
   }?,
   text
}

